package chapter02;

public class plus {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b = 3;
		
		System.out.println("a+b : "+ (a+b));
		System.out.println("a-b : "+ (a-b));
		System.out.println("a*b : "+ (a*b));
		System.out.println("a/b : "+ (a/b));
		System.out.println("a%b : "+ (a%b));
	

	}

}
