package chapter02;

public class plusplus {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 5;
		System.out.println(++num1);
		System.out.println(++num1);
		System.out.println(num1);
		
		System.out.println("==================");
		int num2 = 5;
		System.out.println(num2++);
		System.out.println(num2++);
		System.out.println(num2);
		
		System.out.println("==================");
		int num3 = 5;
		System.out.println(--num3);
		System.out.println(--num3);
		System.out.println(num3);
		
		System.out.println("==================");
		int num4 = 5;
		System.out.println(num4--);
		System.out.println(num4--);
		System.out.println(num4);
	}

}
