package chapter06;

public class mathod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//메서드 예제
		int a = 30; // a라는 변수에 30이라는 숫자를 저장
		everyone(a); // everyone이라는 메서드 선언 후 값을 a의 저장된 값으로 초기화

	}
	            //리턴타입 //메서드이름  //메서드 매개변수
	public static void everyone (int age) { //메서드 작성
		System.out.printf("제 나이는 %d입니다", age);
	}
}
